sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   /*if (isLoading) {
      return;
   }
	if(g_form.getValue("u_what_needed")!=''){
		jslog("What needed already set");
		return;

	}*/
	jslog("OldValue - "+oldValue+" New value - "+newValue);
	g_form.clearOptions("u_what_needed");
	if(newValue === 'hr'){
		jslog("What needed options for Human Resources");
		g_form.addOption("u_what_needed","hr1","Human Resources 1");
		g_form.addOption("u_what_needed","hr2", "Human Resources 2");
		g_form.addOption("u_what_needed", "other","Other");
	}else if(newValue === 'facilities'){
		jslog("What needed option for Facilities");
		g_form.addOption("u_what_needed", "facilities1", "Facilities 1");
		g_form.addOption("u_what_needed", "facilities2", "Facilities 2");
		g_form.addOption("u_what_needed", "other", "Other");
	}else if(newValue === 'legal'){
		jslog("What needed option for Legal");
		g_form.addOption("u_what_needed", "legal1", "Legal1");
		g_form.addOption("u_what_needed", "legal2", "Legal2");
		g_form.addOption("u_what_needed", "other", "Other");
	}else if(newValue === ''){
		jslog("What needed with full option list");
		g_form.addOption("u_what_needed", "hr1", "Human Resources1");
		g_form.addOption("u_what_needed", "hr2", "Human Resources2");
		g_form.addOption("u_what_needed", "facilities1", "Facilities1");
		g_form.addOption("u_what_needed", "facilities2", "Facilities2");
		g_form.addOption("u_what_needed", "legal1", "Legal1");
		g_form.addOption("u_what_needed", "legal2", "Legal2");
		g_form.addOption("u_what_needed", "other", "Other");
	}

   //Type appropriate comment here, and begin script below

}
